import React, {useEffect, useState} from 'react';
import './App.css';
import {Link} from  'react-router-dom';


function ShowInterview(match) {

  useEffect(() => {
  	// when url is called 
    // console.log(match);
  	fetchItem();
  },[]);

  const [item,setItem] = useState([]);

  const fetchItem = async() => {
    const fetchItem = await fetch("http://localhost:3001/api/v1/interviews/" + match.match.params.id );
    const item = await fetchItem.json();
    setItem(item["data"]);
    console.log(item["data"]);
  }
  return (
  	// what to return after api called
    <div>
      <h1><u>Topic: </u></h1> <h1>{item.topic}</h1>
      <h2>Start time: </h2> <h2>{item.schedule_at}</h2>
      <h2>End time: </h2> <h2>{item.end_time}</h2>
      <h2>Role: </h2> <h2>{item.role}</h2>
      <h2>Meeting Link: </h2> <h2>{item.meet_link}</h2>
      <h2>Interviewer: </h2> <h2>{item.user_name}</h2>


    </div>
  );
}

export default ShowInterview;
